import {
    FETCH_PHONES_START,
    FETCH_PHONES_SUCCESS,
    FETCH_PHONES_FAIL,
    LOAD_MORE_PHONES_START,
    LOAD_MORE_PHONES_SUCCESS,
    LOAD_MORE_PHONES_FAIL
} from '../actionTypes'




import {fetchPhones as fetchPhonesApi,
        loadMorePhonesApi as loadMorePhonesApi} from '../api/api'
import {getRenderedPhonesLength} from "../selectors";

export const fetchPhones = () => async (dispatch, getState) => {
    const offset = getRenderedPhonesLength (getState())
    dispatch({
        type: FETCH_PHONES_START
    })
    try{
        const phones = await loadMorePhonesApi({offset})
        dispatch({
            type: FETCH_PHONES_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type:FETCH_PHONES_FAIL,
            payload: err,
            error: true
        })
    }


}

export const loadMorePhones = () => async dispatch => {
    dispatch({
        type: LOAD_MORE_PHONES_START
    })
    try{
        const phones = await fetchPhonesApi()
        dispatch({
            type: LOAD_MORE_PHONES_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type:LOAD_MORE_PHONES_FAIL,
            payload: err,
            error: true
        })
    }


}
